function dialog_alert(text){
    alert(text);
}
function dialog_prompt(question, value){
    prompt(question, value);
}
function dialog_confirm(question){
    confirm(question);
}
function con_log(text){
    console.log(text);
}
function con_info(text){
    console.info(text);
}
function con_error(text){
    console.error(text);
}
function con_warn(text){
    console.warn(text);
}
function anaconda(element, event, functions){
    let ele = document.querySelector(element);
    ele.addEventListener(event, functions);
}
function addclass(element, classname){
    let ele = document.querySelector(element);
    ele.classList.add(classname);
}
function removeclass(element, classname){
    let ele = document.querySelector(element);
    ele.classList.remove(classname);
}
function addattr(element, attribute, value){
    let ele = document.querySelector(element);
    let attr = ele.createAttribute(attribute);
    attr.value = value;
    ele.setAttributeNode(attr);
}
function removeattr(element, attribute){
    let ele = document.querySelector(element);
    ele.removeAttribute(attribute);
}
